mapper.select:
SELECT admin_id, admin_name, password, enable_status
FROM tb_admin
WHERE admin_id = #{adminID}

SELECT admin_id, admin_name, password, profile_img, birthday, gender, phone, enable_status
FROM tb_admin
WHERE admin_id = #{adminID}

mapper.update:
