mapper.select:
SELECT design_id, design_name, teacher_id, create_time, file_address, file_name, enable_status
FROM tb_design
WHERE teacher_id = #{teacherID}

SELECT design_id, design_name, teacher_id, create_time, file_address, file_name, enable_status
FROM tb_design
WHERE design_id = #{designID}

mapper.update:
UPDATE tb_design
SET enable_status = #{enableStatus}
WHERE design_id = #{designID}
